    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        // TODO add your handling code here:
        String course=(String)jComboBox1.getSelectedItem();
        String branch=(String)jComboBox2.getSelectedItem();
        String rollno=jTextField9.getText();
        String name=jTextField10.getText();
        String gender=(String)jComboBox3.getSelectedItem();
        String fathername=jTextField12.getText();
        
        try{
            Class.forName("com.mysql.jdbc.Driver");
            Connection con=DriverManager.getConnection("jdbc:mysql://localhost:3306/srm","root","aA@235689");
            Statement st=con.createStatement();
            if(rollno.isEmpty()){
                JOptionPane.showMessageDialog(null, "Roll number cannot be empty!");
            }
            else{
                 st.executeUpdate("insert into student values('"+rollno +"','"+ course+"','"+ branch +"','"+name+"','"+gender+"','"+fathername+"')");
            JOptionPane.showMessageDialog(null, "Succesfully added a student");
            setVisible(false);
            new adminHome().setVisible(true);
            }
           
        }
        catch(Exception e){
            JOptionPane.showMessageDialog(null, e.toString());
        }
    }                                        

