    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        // TODO add your handling code here:
        String rollno=jTextField1.getText();
        String physicsmarks=jTextField2.getText();
        String chemistrymarks=jTextField3.getText();
        String mathsmarks=jTextField4.getText();
        String dbmsmarks=jTextField6.getText();
        String osmarks=jTextField5.getText();
        try{
             Class.forName("com.mysql.cj.jdbc.Driver");
            Connection con=DriverManager.getConnection("jdbc:mysql://localhost:3306/srm","root","aA@235689");
            Statement st=con.createStatement();
            ResultSet rs=st.executeQuery("select * from student where rollno='"+ rollno +"'");
            if(rs.next()){
               st.executeUpdate("insert into result(rollno,physicsmarks,chemistrymarks,mathsmarks,dbmsmarks,osmarks)values('"+rollno +"','"+ physicsmarks +"','"+chemistrymarks +"','"+mathsmarks+"','"+dbmsmarks+"','"+osmarks+"')");
            JOptionPane.showMessageDialog(null, "Succesfully updated a result");
            setVisible(false);
            new insertnewresult().setVisible(true);
            
            }
            else{
                JOptionPane.showMessageDialog(null, "Rollno doesnot exist.");
            }
        }
        catch(Exception e){
            JOptionPane.showMessageDialog(null,e.toString());
        }
    }                                        

